import * as PIXI from 'pixi.js';
import Action from './actions/Action';
import Interpolation from './Interpolation';
export default class Actions {
    static actions: Array<Action>;
    static moveTo(target: PIXI.DisplayObject, x: number, y: number, seconds: number, interpolation?: Interpolation): Action;
    static fadeTo(target: PIXI.DisplayObject, alpha: number, seconds: number, interpolation?: Interpolation): Action;
    static fadeOut(target: PIXI.DisplayObject, seconds: number, interpolation?: Interpolation): Action;
    static fadeOutAndRemove(target: PIXI.DisplayObject, seconds: number, interpolation?: Interpolation): Action;
    static fadeIn(target: PIXI.DisplayObject, seconds: number, interpolation?: Interpolation): Action;
    static remove(target: PIXI.DisplayObject): Action;
    static delay(seconds: number): Action;
    static runFunc(fn: () => void): Action;
    static scaleTo(target: PIXI.DisplayObject, x: number, y: number, seconds: number, interpolation?: Interpolation): Action;
    static rotateTo(target: PIXI.DisplayObject, rotation: number, seconds: number, interpolation?: Interpolation): Action;
    static tintTo(target: PIXI.DisplayObject, tint: number, seconds: number, interpolation?: Interpolation): Action;
    static sequence(...actions: Array<Action>): Action;
    static parallel(...actions: Array<Action>): Action;
    static repeat(action: Action, times?: number): Action;
    static play(action: Action): void;
    static pause(action: Action): void;
    static clear(target?: PIXI.DisplayObject): void;
    static tick(delta: number): void;
}
